var index = "{{_index}}"

var createdAt = msg.payload['created_at']
msg.payload['created_at'] = new Date(Date.parse(createdAt.replace(/( \+)/, ' UTC$1')));

var operation = {
    "_index": index,
    "_type": "default"
    }

var msg1 = {
    payload: JSON.stringify({"index": operation})
}

var msg2 = {
    payload: JSON.stringify(msg.payload)
}

return [[msg1,msg2]];

delete msg.payload
msg.complete = true

return msg;
